import * as React from 'react';
import Avatar from '@mui/material/Avatar';
import Button from '@mui/material/Button';
import CssBaseline from '@mui/material/CssBaseline';
import TextField from '@mui/material/TextField';
import Box from '@mui/material/Box';
import LockOutlinedIcon from '@mui/icons-material/LockOutlined';
import Typography from '@mui/material/Typography';
import Container from '@mui/material/Container';
import { createTheme, ThemeProvider } from '@mui/material/styles';
import axios from 'axios';
import { useState } from 'react';
import BoyIcon from '@mui/icons-material/Boy';
import List from '@mui/material/List';
import ListItem from '@mui/material/ListItem';
import ListItemButton from '@mui/material/ListItemButton';
import ListItemIcon from '@mui/material/ListItemIcon';
import ListItemText from '@mui/material/ListItemText';

const theme = createTheme();

export default function Manager() {

  const [isLogin, setIsLogin] = useState(true);
  const [pass, setPassword] = useState("")
  const [employees, setEmployees] = useState([{name:"aaa"},{name:"bbb"},{name:"ccc"}])

  const handleSubmit = (event) => {
    event.preventDefault();
    axios.get(`https://localhost:44382/api/Mamager/${pass}`)
      .then(res => {
        console.log(res.data);
        if (res.ok)
          setIsLogin(true)
        axios.get(`https://localhost:44382/api/Employee`)
          .then(res => {
            setEmployees(res.data)
          }
          )

      }).catch(err => console.log(err))


  };

  const activateAlgorithm = (event) => {
    event.preventDefault();
    axios.get(`https://localhost:44382/api/Mamager/${pass}`)
      .then(res => {
        console.log(res.data);
      }).catch(err => console.log(err))
  };

  return (
    <ThemeProvider theme={theme}>
      <Container component="main" maxWidth="xs">
        <CssBaseline />
        <Box
          sx={{
            marginTop: 8,
            display: 'flex',
            flexDirection: 'column',
            alignItems: 'center',
          }}
        >
          <Avatar sx={{ m: 1, bgcolor: 'lightskyblue' }}>
            <LockOutlinedIcon />
          </Avatar>
          <Typography component="h1" variant="h5">
            התחברות
          </Typography>
          <Box component="form" onSubmit={handleSubmit} noValidate sx={{ mt: 1 }}>
            <TextField
              margin="normal"
              required
              fullWidth
              name="password"
              label="סיסמא"
              type="password"
              id="password"
              autoComplete="current-password"
              onChange={(e) => setPassword({ ...e.target.value })}
            />

            <Button
              type="submit"
              fullWidth
              variant="contained"
              sx={{ mt: 3, mb: 2 }}
            >
              התחברות
            </Button>

          </Box>
          {isLogin &&
            <>

              <List
                sx={{ width: '100%', maxWidth: 360, bgcolor: 'background.paper' }}
                aria-label="contacts"
              >
                {employees.map((item, index) => (
                  <ListItem disablePadding>
                    <ListItemButton>
                      <ListItemIcon>
                        <BoyIcon />
                      </ListItemIcon>
                      <ListItemText primary={item.Name} />
                    </ListItemButton>
                  </ListItem>
                ))}
              </List>

              <TextField
                margin="normal"
                required
                fullWidth
                name="start"
                label="החל מ"
                type="number"
                id="start"
                autoComplete="current-start"
              />
              <TextField
                margin="normal"
                required
                fullWidth
                name="end"
                label="עד ל"
                type="number"
                id="end"
                autoComplete="current-end"
              /> <Button
                type="button"
                fullWidth
                variant="contained"
                sx={{ mt: 3, mb: 2 }}
                onClick={activateAlgorithm}
              >
                הפעלת האלגוריתם
              </Button>
            </>}
        </Box>

      </Container>
    </ThemeProvider>
  );
}
