import * as React from 'react';
import Avatar from '@mui/material/Avatar';
import Button from '@mui/material/Button';
import CssBaseline from '@mui/material/CssBaseline';
import Box from '@mui/material/Box';
import LockOutlinedIcon from '@mui/icons-material/LockOutlined';
import Container from '@mui/material/Container';
import { ThemeProvider } from '@mui/material/styles';
import axios from 'axios';
import Select from '@mui/material/Select';
import MenuItem from '@mui/material/MenuItem';
import { createTheme } from '@material-ui/core';
import { useState } from 'react';
import { InputLabel } from '@mui/material';
import { useEffect } from 'react';


const theme = createTheme();

export default function CustomerService() {
    const [service, setService] = useState("בחר");
    const arrayServices=[];


    const sendRequest = (event) => {
        event.preventDefault();

        axios.put(`https://localhost:44382/api/Service/${service}`)
            .then(res => {
                console.log(res.data);
            }).catch(err => console.log(err))
    };

    const handleChange = (event) => {
        setService(event.target.value);
    };

    useEffect=()=>{
        axios.get(`https://localhost:44382/api/Service`)
            .then(res => {
                console.log(res.data);
                arrayServices=res.data;
            }).catch(err => console.log(err))
    }
    
    return (
        <ThemeProvider theme={theme}>
            <Container component="main" maxWidth="xs">
                <CssBaseline />
                <Box
                    sx={{
                        marginTop: 8,
                        display: 'flex',
                        flexDirection: 'column',
                        alignItems: 'center',
                    }}
                >
                    <Avatar sx={{ m: 1, bgcolor: 'lightskyblue' }}>
                        <LockOutlinedIcon />
                    </Avatar>
                    <>
                        <InputLabel id="demo-simple-select-label">בחר את סוג השירות הרצוי</InputLabel> 
                        <Box sx={{ minWidth: 200 }}>
                            <Select
                                labelId="demo-simple-select-label"
                                id="demo-simple-select"
                                onChange={handleChange}
                                value={service}
                            >
                                {arr.map((item, index) => (
                            <ListItem disablePadding>
                                <ListItemButton>
                                    <ListItemIcon>
                                        <BoyIcon />
                                    </ListItemIcon>
                                    <ListItemText primary={item.Name} />
                                </ListItemButton>
                            </ListItem>
                        ))}
                                <MenuItem value={10}>Ten</MenuItem>
                                <MenuItem value={20}>Twenty</MenuItem>
                                <MenuItem value={30}>Thirty</MenuItem>
                            </Select>
                        </Box>
                        <h1 align="center">לידיעתך</h1><h3 align="center">
                            החברה מתחייבת לספק שירות תוך שלושה ימי עסקים
                            החברה תיידע אותך יום לפני על הזמן המשוער של הגעת הטכנאי
                        </h3><Button
                            type="button"
                            fullWidth
                            variant="contained"
                            sx={{ mt: 3, mb: 2 }}
                            onClick={sendRequest}
                        >
                            שלח בקשה
                        </Button>
                    </>
                </Box>
            </Container>
        </ThemeProvider>

    )


}
